/* the given two rooms are adjacent if they differ by only one room in the x or y direction */
adjacent1(X1, Y1, X2, Y2) :- room(X1, Y1), room(X2, Y2), Y1 is Y2, X1 is X2-1.
adjacent2(X1, Y1, X2, Y2) :- room(X1, Y1), room(X2, Y2), Y1 is Y2, X2 is X1-1.
adjacent3(X1, Y1, X2, Y2) :- room(X1, Y1), room(X2, Y2), X1 is X2, Y1 is Y2-1.
adjacent4(X1, Y1, X2, Y2) :- room(X1, Y1), room(X2, Y2), X1 is X2, Y2 is Y1-1.

/* adjacencies used for detection of wumpus and gold */
diag(X1, Y1, X2, Y2) :- room(X1, Y1), room(X2, Y2), 1 is abs(Y1-Y2), 1 is abs(X1-X2).
across1(X1, Y1, X2, Y2) :- room(X1, Y1), room(X2, Y2), 2 is abs(Y1-Y2), X1 is X2.
across2(X1, Y1, X2, Y2) :- room(X1, Y1), room(X2, Y2), X1 is X2-2, Y1 is Y2.
across(X1, Y1, X2, Y2) :- across1(X1, Y1, X2, Y2).
across(X1, Y1, X2, Y2) :- across2(X1, Y1, X2, Y2).
/*room(X, Y) :- integer(X), integer(Y), X < 50, X > 0, Y < 50, Y > 0.*/

adjacent(X1, Y1, X2, Y2) :- adjacent1(X1, Y1, X2, Y2).
adjacent(X1, Y1, X2, Y2) :- adjacent2(X1, Y1, X2, Y2).
adjacent(X1, Y1, X2, Y2) :- adjacent3(X1, Y1, X2, Y2).
adjacent(X1, Y1, X2, Y2) :- adjacent4(X1, Y1, X2, Y2).

/* boundary conditions */
/*wallWest(X, Y) :- X is 0.*/
/*wallEast(X, Y) :- X is 9.*/
/*wallNorth(X, Y) :- Y is 0.*/
/*wallSouth(X, Y) :- Y is 9.*/

cornerNW(X, Y) :- wallWest(X, Y), wallNorth(X, Y).
cornerNE(X, Y) :- wallEast(X, Y), wallNorth(X, Y).
cornerSW(X, Y) :- wallWest(X, Y), wallSouth(X, Y).
cornerSE(X, Y) :- wallEast(X, Y), wallSouth(X, Y).

/* there is a pit in X,Y if there is no adjacent room that does not have a breeze. */
/*adjbreeze(X, Y) :- breeze(U,V), adjacent(X,Y,U,V).*/
/*nobreeze(X, Y) :- not(adjbreeze(X, Y)).*/

:-dynamic(breeze/2). 
:-dynamic(stench/2). 
:-dynamic(glitter/2). 

/*breeze(X, Y) :- X is -1, Y is -1. dummy placeholder */

/*unsafe(X, Y) :- adjacent(U, V, X, Y), breeze(U, V).*/
nobreeze(X, Y) :- call(breeze(X, Y)) -> fail ; true.

/* there is no pit at X, Y if one of the surrounding rooms was visited and was found to have no breeze. */
/* note that "visited" here means "we visited this room and did not perish." */
nopit(X, Y) :- visited(U, V), adjacent(U, V, X, Y), nobreeze(U, V).
nopit(X, Y) :- visited(X, Y).

/* there is certainly a pit at X, Y if 1) there is no unvisited adjacent room, and 2) every visited room has a breeze (equivalently, there is no adjacent visited room that is without a breeze). */
unvisited(X, Y) :- call(visited(X, Y)) -> fail; true.
unvisitedadjacent(X, Y) :- adjacent(U, V, X, Y), unvisited(U, V).
pit(X, Y) :- call(unvisitedadjacent(X, Y)) -> fail; true, call(notallbreezes(X, Y)) -> fail; true.
notallbreezes(X, Y) :- adjacent(U, V, X, Y), nobreeze(U, V).


crapwumpus(X, Y) :- diag(X+1, Y, X, Y+1), stench(X+1, Y), stench(X, Y+1), visited(X+1, Y+1). /* w, s, s, v */
crapwumpus(X, Y) :- diag(X-1, Y, X, Y-1), stench(X-1, Y), stench(X, Y-1), visited(X-1, Y-1). /* v, s, s, w */
crapwumpus(X, Y) :- diag(X-1, Y, X, Y+1), stench(X-1, Y), stench(X, Y+1), visited(X-1, Y+1). /* s, w, v, s */
crapwumpus(X, Y) :- diag(X, Y-1, X+1, Y), stench(X, Y-1), stench(X+1, Y), visited(X+1, Y-1). /* s, v, w, s */

wumpus(X, Y) :- diag(A, B, C, D), adjacent(X, Y, A, B), adjacent(X, Y, C, D), stench(A, B), stench(C, D), unvisited(X, Y), adjacent(E, F, A, B), adjacent(E, F, C, D), visited(E, F).
wumpus(X, Y) :- across(A, B, C, D), adjacent(X, Y, A, B), adjacent(X, Y, C, D), stench(A, B), stench(C, D).
nostench(X, Y) :- call(stench(X, Y)) -> fail ; true.
nowumpus(X, Y) :- visited(U, V), adjacent(U, V, X, Y), nostench(U, V).
nowumpus(X, Y) :- visited(X, Y).

safe(X, Y) :- nopit(X, Y), nowumpus(X, Y).

unsafe(X, Y) :- call(nopit(X, Y)) -> fail; true.
unsafe(X, Y) :- call(nowumpus(X, Y)) -> fail; true.

deadly(X, Y) :- pit(X, Y).
deadly(X, Y) :- wumpus(X, Y).

gold(X, Y) :- diag(A, B, C, D), adjacent(X, Y, A, B), adjacent(X, Y, C, D), glitter(A, B), glitter(C, D), unvisited(X, Y), adjacent(E, F, A, B), adjacent(E, F, C, D), visited(E, F).
gold(X, Y) :- across(A, B, C, D), adjacent(X, Y, A, B), adjacent(X, Y, C, D), glitter(A, B), glitter(C, D).

noglitter(X, Y) :- call(glitter(X, Y)) -> fail ; true.
nogold(X, Y) :- visited(U, V), adjacent(U, V, X, Y), noglitter(U, V).
useless(X, Y) :- nogold(X, Y).
promising(X, Y) :- call(nogold(X, Y)) -> fail; true, adjacent(U, V, X, Y), glitter(U, V).
golden(X, Y) :- gold(X, Y).
